#include <iostream>using namespace std;int celcius(int);int main(){  int farenheit,       conversion;          cout << "Farenheit\tCelcius\n"         << "-----------------------\n";    for (int farenheit = 0; farenheit <= 20; farenheit++)    {        conversion = celcius(farenheit);        cout << farenheit << "\t\t" << conversion << endl;    }}int celcius(int farenheit){    return (5.0/9.0)*((double)farenheit - 32.0);}